function CBIG_pFIC_check_example_results(output_path, reference_output_path)
% This function takes in outputs generated by users and compares them with
% the reference outputs
% Written by Shaoshi Zhang and CBIG under MIT license: https://github.com/ThomasYeoLab/CBIG/blob/master/LICENSE.md

user_training_matrix = csvread([output_path '/training/training_1.csv']);
reference_training_matrix = csvread([reference_output_path '/training/training_1.csv']);

if sum(abs(user_training_matrix(:) - reference_training_matrix(:))) < 1e-6
   disp('Passed! Results are the same as the reference outputs.')
else
   error(['Failed. Results differ too much from the reference outputs. Please ' ...
       'check the hardware and software versions.'])
end

end
